import React, { useEffect, useState } from "react";
import axios from 'axios';


export const NewsList = () => {
  const [news , setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () =>{
      try{
        const response = await axios.get('http://localhost:7001/api/news')
        setNews(response.data)
      } catch(error) {
        console.error(error);
      }
    }
    fetchNews()
  }, [])

  return(
    <div className="rounder border-2 px-4 mx-20 my-10">
      <h3 className="text-4xl">Новости</h3>
    {news && news.length > 0 ? (
      <ul className="flex border-2 rounder">
        {news.map((item) => (
          <li key={item.id} className="border-2 rounder my-3 mx-3 w-56 h-72 grid">
            <img src={item.icon} alt=""  className="block mx-auto w-40"/>
            <h4 className="text-l text-center h-6">{item.title}</h4>
            <p className="text-center text-xs h-5">{item.text}</p>
          </li>
        ))}
      </ul>
    ) : (
      <p className="text-red-600">Нет новостей для отображения</p>
    )}
  </div>
  )
}
