import React, { useEffect, useState } from "react";
import axios from 'axios';

export const NewsList = () => {
  const [news , setNews] = useState([]);

  useEffect(() => {
    const fetchNews = async () =>{
      try{
        const response = await axios.get('http://localhost:7001/api/news')
        setNews(response.data)
      } catch(error) {
        console.error(error);
      }
    }

    fetchNews()


  }, [])

  return(
    <div class="rounder border-2 px-4 mx-48 my-10">
      <h3 class="text-4xl">Новости</h3>
    {news && news.length > 0 ? (
      <ul>
        {news.map((item) => (
          <li key={item.id} class="border-2 rounder my-3">
            <h4 class="text-2xl text-center">{item.title}</h4>
            <p>{item.text}</p>
          </li>
        ))}
      </ul>
    ) : (
      <p>Нет новостей для отображения</p>
    )}
  </div>
  )
}
