import React, { useState } from "react";
import axios from 'axios';

const AddCourseForm = () => {
  const [formData, setFormData] = useState({
    courseName: '',
    courseIcon: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:7001/api/course', formData);
      // После успешной отправки данных, перенаправляем пользователя на страницу со списком курсов
      window.location = '/';
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mx-auto mt-10">
      <h1 className="text-3xl font-semibold mb-4">Добавить новый курс</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label htmlFor="courseName" className="block mb-1">Название курса:</label>
          <input type="text" id="courseName" name="courseName" value={formData.courseName} onChange={handleChange} className="border border-gray-300 px-3 py-2 rounded-md w-full" />
        </div>
        <div className="mb-4">
          <label htmlFor="courseIcon" className="block mb-1">Иконка курса (URL):</label>
          <input type="text" id="courseIcon" name="courseIcon" value={formData.courseIcon} onChange={handleChange} className="border border-gray-300 px-3 py-2 rounded-md w-full" />
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600">Добавить курс</button>
      </form>
    </div>
  );
};

export default AddCourseForm;
